{"filter":false,"title":"ability.rb","tooltip":"/thetransylvaniandeliveryservice/app/models/ability.rb","undoManager":{"mark":2,"position":2,"stack":[[{"start":{"row":2,"column":0},"end":{"row":29,"column":74},"action":"remove","lines":["","  def initialize(user)","    # Define abilities for the passed in user here. For example:","    #","    #   user ||= User.new # guest user (not logged in)","    #   if user.admin?","    #     can :manage, :all","    #   else","    #     can :read, :all","    #   end","    #","    # The first argument to `can` is the action you are giving the user","    # permission to do.","    # If you pass :manage it will apply to every action. Other common actions","    # here are :read, :create, :update and :destroy.","    #","    # The second argument is the resource the user can perform the action on.","    # If you pass :all it will apply to every resource. Otherwise pass a Ruby","    # class of the resource.","    #","    # The third argument is an optional hash of conditions to further filter the","    # objects.","    # For example, here the user can only update published articles.","    #","    #   can :update, Article, :published => true","    #","    # See the wiki for details:","    # https://github.com/CanCanCommunity/cancancan/wiki/Defining-Abilities"],"id":2},{"start":{"row":2,"column":0},"end":{"row":9,"column":3},"action":"insert","lines":["class Ability","  include CanCan::Ability","","  def initialize(user)","    user ||= User.new # guest user (not logged in)","    can :manage, User, id: user.id","  end","end"]}],[{"start":{"row":0,"column":0},"end":{"row":1,"column":25},"action":"remove","lines":["class Ability","  include CanCan::Ability"],"id":3},{"start":{"row":0,"column":0},"end":{"row":1,"column":0},"action":"remove","lines":["",""]}],[{"start":{"row":8,"column":2},"end":{"row":9,"column":3},"action":"remove","lines":["end","end"],"id":4}]]},"ace":{"folds":[],"scrolltop":0,"scrollleft":0,"selection":{"start":{"row":9,"c